import React, {useState, useRef, useEffect} from "react";
import logo from './logo.svg';
import './App.css';
import { MapContainer, TileLayer, useMap, Marker, Popup } from 'react-leaflet' ;
import axios from "axios";
import {Alert, Spinner} from "react-bootstrap";
import useSWR from "swr";
import {circle, Icon, rectangle} from "leaflet/dist/leaflet-src.esm";
import { GeoJSON } from 'react-leaflet';
import {preventDefault} from "leaflet/src/dom/DomEvent";
import {clear} from "@testing-library/user-event/dist/clear";
import {cleanup} from "@testing-library/react";
import DrawingArea from './DrawingArea';















const MyData = (props, event) => {
  // create state variable to hold data when it is fetched




  let [data, setData] = useState();

    // useEffect to fetch data on mount

    useEffect(() => {
      // async function!

      let getData = async () => {
        // 'await' the data
        let response = await axios.get(props.name);
        // save data to state
        setData(response.data);
      };

      getData();


    }, [props.name]);



  // render react-leaflet GeoJSON when the data is ready
  if (data) {
    //return <input type={"text"} value={name} onChange={updateName}/>;

   return <GeoJSON data={data} />


  } else {
    //return <input type={"text"} value={name} onChange={updateName}/>;
    return null;


  }

};


function App(event) {

  const drawingareaRef = useRef(null);
  const ctx = useRef(null);
  const mapcontainerRef = useRef(null)

