import {useLeafletContext} from "@react-leaflet/core";
import * as L from "leaflet";
import {useEffect} from "react";
import canvasLayer from "leaflet-canvas-layer"
import {useMap, useMapEvent, useMapEvents} from "react-leaflet";
import {Handler} from "leaflet/dist/leaflet-src.esm";
import Canvas from "./Canvas";
import {getPosition} from "leaflet/src/dom/DomUtil";

L.GridLayer.Custom_Canvas = L.GridLayer.extend({
    createTile: function (coords){
        var tile = document.createElement('canvas');

        var tileSize = this.getTileSize();
        tile.setAttribute('width', tileSize.x);
        tile.setAttribute('height', tileSize.y);

        var ctx = tile.getContext('2d');

        // Draw whatever is needed in the canvas context
        // For example, circles which get bigger as we zoom in
        ctx.beginPath();
        ctx.arc(tileSize.x/2, tileSize.x/2, 4 + coords.z*4, 0, 2*Math.PI, false);
        ctx.fill();

        return tile;
    }
})




const Map_paint = () => {


    const ctx = useLeafletContext()



    const cnvs_overlay = L.GridLayer.Custom_Canvas




    useEffect(() => {








    const canvas_container = ctx.overlayContainer || ctx.map







        canvas_container.addLayer(cnvs_overlay)



        return () => {
            canvas_container.removeLayer(cnvs_overlay)


        }

        })
}

export default Map_paint
