import React, {useState, useEffect} from "react";
import logo from './logo.svg';
import './App.css';
import { MapContainer, TileLayer, useMap, Marker, Popup } from 'react-leaflet' ;
import axios from "axios";
import {Alert, Spinner} from "react-bootstrap";
import useSWR from "swr";
import {Icon} from "leaflet/dist/leaflet-src.esm";
import { GeoJSON } from 'react-leaflet';
import {preventDefault} from "leaflet/src/dom/DomEvent";
import {clear} from "@testing-library/user-event/dist/clear";
import {cleanup} from "@testing-library/react";




var url = "http://127.0.0.1:8000/api/world"








const MyData = (props, event) => {
  // create state variable to hold data when it is fetched




  let [data, setData] = React.useState();

    // useEffect to fetch data on mount

    useEffect(() => {
      // async function!

      let getData = async () => {
        // 'await' the data
        let response = await axios.get(url + "/" + props.name);
        // save data to state
        setData(response.data);
      };
      getData();


    }, [props.name]);



  // render react-leaflet GeoJSON when the data is ready
  if (data) {
    //return <input type={"text"} value={name} onChange={updateName}/>;

    return <GeoJSON data={data} />;


  } else {
    //return <input type={"text"} value={name} onChange={updateName}/>;
    return null;


  }

};


function App(event) {



  let [name, setName] = useState("");





  let updateName = (event) => {

    if (event.key === 'Enter'){
      event.preventDefault();
    setName(event.target.value);

    console.log(name)

  };

  //else {setName("")}
  //return <MyData name={name + "/"}/>
};

useEffect(() => {
    console.log("value of 'name' changed to", name);

  }, [name]);

  return (
  <div>
  <input type={"text"} onKeyDown={updateName}/>


    <MapContainer center={[51.505, -0.09]} zoom={2} scrollWheelZoom={true} onKeyDown={updateName}>
  <TileLayer
    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
  />

      <MyData name={name + "/"}/>

  </MapContainer>


</div>

  );
}

export default App;
