import React, {useState, useEffect} from "react";
import logo from './logo.svg';
import './App.css';
import { MapContainer, TileLayer, useMap, Marker, Popup } from 'react-leaflet' ;
import axios from "axios";
import {Alert, Spinner} from "react-bootstrap";
import useSWR from "swr";
import {Icon} from "leaflet/dist/leaflet-src.esm";
import { GeoJSON } from 'react-leaflet';




var url = "http://127.0.0.1:8000/api/world/Russia/"







const MyData = (props) => {
  // create state variable to hold data when it is fetched
  let url_1 = ""





  const [name, setName] = useState("");

  const updateName = (event) => {

    if (event.key === 'Enter'){
    setName(event.target.value)}
    else {setName(event.target.value)};



  };

  if (name === "") {
    url_1 = url
  } else {
    url_1 = url.concat(name)
  };




  const [data, setData] = React.useState();

    // useEffect to fetch data on mount
  useEffect(() => {
    // async function!
    const getData = async () => {
      // 'await' the data
      const response = await axios.get("http://127.0.0.1:8000/api/world/Russia/");
      // save data to state
      setData(response.data);
    };
    getData();
  }, []);

  console.log(url_1)

  // render react-leaflet GeoJSON when the data is ready
  if (data) {
    //return <input type={"text"} value={name} onChange={updateName}/>;
    return <GeoJSON data={data} />;


  } else {
    //return <input type={"text"} value={name} onChange={updateName}/>;
    return null;


  }
};


function App() {







  return (
    <MapContainer center={[51.505, -0.09]} zoom={2} scrollWheelZoom={true}>
  <TileLayer
    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
  />

      <MyData/>

</MapContainer>



  );
}

export default App;
