import logo from './logo.svg';
import './App.css';
import { MapContainer, TileLayer, useMap, Marker, Popup } from 'react-leaflet' ;
import axios from "axios";
import {Alert, Spinner} from "react-bootstrap";
import useSWR from "swr";
import {Icon} from "leaflet/dist/leaflet-src.esm";
import { GeoJSON } from 'react-leaflet';
import {preventDefault} from "leaflet/src/dom/DomEvent";




var url = "http://127.0.0.1:8000/api/world"





const MyData_2 = (props) => {
  const [name, setName] = useState("");

  const updateName = (event) => {

    //if (event.key === 'Enter'){
    setName(event.target.value);
   //else {setName("")};


  //console.log(name)
}
return (


    <input type={"text"}  onChange={updateName}/>)
};



const MyData = (event, props) => {
  // create state variable to hold data when it is fetched
  let url_1 = ""








  //};

  //if (MyData_2.name === "") {
    //url_1 = url
  //} else {
    //url_1 = url.concat(MyData_2.name + "/")
  //};




  const [data, setData] = React.useState();

    // useEffect to fetch data on mount

    useEffect(() => {
      // async function!

      const getData = async () => {
        // 'await' the data
        const response = await axios.get(url + "/" + props.name_1);
        // save data to state
        setData(response.data);
      };
      getData();
    }, []);



  // render react-leaflet GeoJSON when the data is ready
  if (data) {
    //return <input type={"text"} value={name} onChange={updateName}/>;
    return <GeoJSON data={data} />;


  } else {
    //return <input type={"text"} value={name} onChange={updateName}/>;
    return null;


  }
};


function App() {

  const forceUpdate = (event) => {
    if (event.key === "Enter"){
      return MyData
    }
    else {
      return MyData}
  };

  const [name, setName] = useState("");



  const updateName = (event) => {

    if (event.key === 'Enter'){
      event.preventDefault();
    setName(event.target.value)

    console.log(name)

  }
  //else {setName("")}

};

  return (
  <div onKeyDown={updateName} onChange={updateName}>
  <input type={"text"} onChange={updateName} onKeyDown={updateName}/>


    <MapContainer center={[51.505, -0.09]} zoom={2} scrollWheelZoom={true} onKeyDown={updateName}>
  <TileLayer
    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
  />

      <MyData onKeyDown={forceUpdate} onChange={updateName} name_1={name + "/"} />

  </MapContainer>


</div>

  );
}

export default App;
