import * as L from "leaflet";
import {useEffect} from "react";
import canvasLayer from "leaflet-canvas-layer"
import {useMap, useMapEvent, useMapEvents} from "react-leaflet";
import {Handler} from "leaflet/dist/leaflet-src.esm";






const Map_paint = (info) => {


    const ctx = useLeafletContext()




    useEffect((props) => {

        const bounds = L.latLng(props.center).toBounds(props.size)
        const container = ctx.map

        const cl = new L.Rectangle(bounds)







        container.addLayer(cl)



        return () => {
            container.removeLayer(cl)

        }

        })
}

export default Map_paint
