import React, {useState, useRef, useEffect} from "react";
import logo from './logo.svg';
import './App.css';
import { MapContainer, TileLayer, SVGOverlay,Polygon,LayerGroup,LayersControl, useMap, Marker, Popup } from 'react-leaflet' ;
import axios from "axios";
import { GeoJSON } from 'react-leaflet';
import Canvas from "./Canvas";
import MyMap from "./copmponents/MyMap";
import CanvasOverlay from 'react-leaflet-canvas-overlay';
import * as L from "leaflet";
import MapPaint from "./copmponents/MapPaint";
import CcanvasOverlay from "./copmponents/CanvasOverlay";
import LSquare from "./copmponents/Lcanvas";
import {createLeafComponent, useLeafletContext} from "@react-leaflet/core";
import Canvas_hook from "./copmponents/Lcanvas";
import Map_paint from "./DrawingArea";
import canvasLayer from "leaflet-canvas-layer";
import Cnvs_overlay from "./cnvs_overlay";




const canvass = document.createElement('canvas');



function createCanvasOverlay()
 {
   // Create a blank div where we are going to put the canvas into.
    var canvasContainer = document.createElement('div');
   // Add the div into the document
    document.body.appendChild(canvasContainer);
    canvasContainer.style.position="absolute";
    // Set to 100% so that it will have the dimensions of the document
    canvasContainer.style.left="0px";
    canvasContainer.style.top="0px";
    canvasContainer.style.width="100%";
    canvasContainer.style.height="100%";
    // Set to high index so that this is always above everything else
    // (might need to be increased if you have other element at higher index)
    canvasContainer.style.zIndex="1000";

    // Now we create the canvas
    const myCanvas = document.createElement('canvas');
    myCanvas.style.width = canvasContainer.scrollWidth+"px";
    myCanvas.style.height = canvasContainer.scrollHeight+"px";
    // You must set this otherwise the canvas will be streethed to fit the container
    myCanvas.width=canvasContainer.scrollWidth;
    myCanvas.height=canvasContainer.scrollHeight;
    myCanvas.style.overflow = 'visible';
    myCanvas.style.position = 'absolute';
    // Add int into the container
    canvasContainer.appendChild(myCanvas);
 }




















const MyData = (props, event) => {
  // create state variable to hold data when it is fetched




  let [data, setData] = useState();

    // useEffect to fetch data on mount

    useEffect(() => {
      // async function!

      let getData = async () => {
        // 'await' the data
        let response = await axios.get(props.name);
        // save data to state
        setData(response.data);
      };

      getData();


    }, [props.name]);



  // render react-leaflet GeoJSON when the data is ready
  if (data) {
    //return <input type={"text"} value={name} onChange={updateName}/>;

   return <GeoJSON data={data} />


  } else {
    //return <input type={"text"} value={name} onChange={updateName}/>;
    return null;


  }

};


const bounds = [
  [51.49, -10],
  [1, 50],
]

const polygon = [
  [51.515, -0.09],
  [51.52, -0.1],
  [51.52, -0.12],
]

const fillBlueOptions = { fillColor: 'blue' }
const blackOptions = { color: 'black' }
const limeOptions = { color: 'lime' }
const purpleOptions = { color: 'purple' }
const redOptions = { color: 'red' }

const canvas = document.createElement('canvas');
const p1 = L.point(10, 10);
const p2 = L.point(40, 60);




function App(event) {



    const canvasRef = useRef(null );
    const contextRef = useRef(null);
    const [isDrawing, setIsDrawing] = useState(false);





  let [name, setName] = useState("http://127.0.0.1:8000/api/world/");


  let updateName = (event) => {

    if (event.key === 'Enter' && event.target.value.slice(-4) != "1914" && event.target.value != "German Empire") {
      event.preventDefault();
      setName("http://127.0.0.1:8000/api/world/" + event.target.value)
    }
    ;

    if (event.key === 'Enter' && event.target.value === "German Empire") {
      event.preventDefault();
      setName("http://127.0.0.1:8000/api/german_empire")
    }
    ;

    if (event.key === 'Enter' && event.target.value.slice(-4) === "1914") {
      event.preventDefault();
      setName("http://127.0.0.1:8000/api/world1914/" + event.target.value.slice(0, -4))
    }

    //else {setName("")}
    //return <MyData name={name + "/"}/>
  };







  useEffect(() => {
    console.log("value of 'name' changed to", name.slice(0, 42));
    console.log()







  }, [name]);

    useEffect(() => {
        const canvas = canvasRef.current;


        const context = canvas.getContext("2d");
        context.lineCap = "round";
        context.strokeStyle = "black";
        context.lineWidth = 5;
        contextRef.current = context;
    }, []);

    const startDrawing = ({nativeEvent}) => {
        const{offsetX, offsetY} = nativeEvent;
        contextRef.current.beginPath();
        contextRef.current.moveTo(offsetX, offsetY);
        contextRef.current.lineTo(offsetX, offsetY);
        contextRef.current.stroke();
        setIsDrawing(true);
        nativeEvent.preventDefault();
    };

    const draw = ({nativeEvent}) => {
        if(!isDrawing){
            return;
        }
        const{offsetX, offsetY} = nativeEvent;
        contextRef.current.lineTo(offsetX, offsetY);
        contextRef.current.stroke();
        nativeEvent.preventDefault();
    };

    const stopDrawing = () => {
        contextRef.current.closePath();
        setIsDrawing(false);


    };


  let NewData = (event) => {

    if (event.key === "Enter" && event.target.value.slice(-4) != "1914" && event.target.value != "German Empire") {

    }

    return <MyData name={name + "/" + "?format=json"}/>
  }

  if (event.key === "Enter" && event.target.value === "German Empire") {



  return <MyData name={name}/>
}

  if (event.key === "Enter" && event.target.value.slice(-4) === "1914") {
    return <MyData name={name.slice(0, 42) + "/"}/>
  };


  const center = [51.505, -0.09]


  return (

  <div style={{"display" : "grid"}}>
    <Canvas>

    </Canvas>
  <button>
  Activate Lasers
  </button>

  <form>
  <link rel="stylesheet" href="style.css"/>
    <label  htmlFor="country">Country:</label>
  <input  id="country" type={"text"} placeholder={"type a country here"} onKeyDown={updateName}/>
  </form>


    <MapContainer  center={[51.505, -0.09]} zoom={2} scrollWheelZoom={true} onKeyDown={updateName}>


  <TileLayer
    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
  />

    <Map_paint ref={canvasRef}
                onMouseDown={startDrawing}
                onMouseMove={draw}
                onMouseUp={stopDrawing}
                onMouseLeave={stopDrawing}/>

       <NewData/>

      <SVGOverlay attributes={{ stroke: 'red' }} bounds={bounds}>
      <rect x="0" y="0" width="100%" height="100%" fill="blue" />
      <circle r="5" cx="10" cy="10" fill="red" />
      <text x="50%" y="50%" stroke="white">
        text
      </text>
    </SVGOverlay>
      <Polygon pathOptions={purpleOptions} positions={polygon} />






  </MapContainer>


</div>

  );
}

export default App;
